mapper.select:
select
count(1)
from goods
where 1=1

	and ID = #{id}


	and productNo like CONCAT('%',#{productno},'%' )


	and productName like CONCAT('%',#{productname},'%' )


	and productType = #{producttype}


	and productStandard like CONCAT('%',#{productstandard},'%' )


	and unit = #{unit}


	and price = #{price}

